mapper.insert:
		INSERT INTO branch_pay_tb(
 			branch_owner_cd
			, room_cd
			, seat_cd
			, member_cd
			, pay_amount
			, discount_amount
			, total_amount
			, pay_date
			, pay_due
			)
			
			
		VALUES(
 			#{branch_owner_cd}
			, #{room_cd}
			, #{seat_cd}
			, #{member_cd}
			, #{pay_amount}
			, #{discount_amount}
			, #{total_amount}
			, #{pay_date} 
			, #{pay_due}
			

		)

mapper.update:
		UPDATE branch_member_tb 
            SET 
            seat_cd = #{seat_cd}
            , member_date = CURRENT_DATE()
            , member_regi_date = now()
            , member_end_date = #{pay_date}
        WHERE member_cd = #{member_cd}
